#include <stdint.h>
#include <ft.h>

uint32_t	ft_rand(void)
{
	static uint32_t	*x = 0;

	if (!x)
		x = ft_get_seed();
	*x ^= *x << 13;
	*x ^= *x >> 17;
	*x ^= *x << 5;
	return (*x);
}
